mapper.select:
select *
from file_resource
where file_md5 = #{fileMd5}
  and module = #{module}
  and is_chunk = 1
order by id desc limit 1

mapper.update:
update file_resource


        upload_status = #{uploadStatus},


        url = #{url},

    update_time = #{updateTime}

where file_md5 = #{fileMd5}
  and module = #{module}
  and is_chunk = 1

mapper.select:
select * from file_resource where
id in

    #{item}
