mapper.select:
select count(*)
from log_view

    where create_time > #{createTime}

select count(distinct ip,border_name,border_version,device_manufacturer,device_type,os_version)
from log_view

    where create_time > #{createTime}

mapper.update:
update log_view


        country = #{country},


        region = #{region},


        city = #{city},

    update_time = #{updateTime}

where id = #{id}
